#include<vector>
#include<iostream>
using namespace std;
//异位词：找字母的个数
class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
    vector<int> a;
    vector<char>b;//p的字符
    vector<char>d;
  
    for(int i=0;i<p.size();i++)
    {
        b.push_back(p[i]);
    }
    int c[26]={0};
    if(p.size()>s.size())
        return vector<int>();
    char *left=&s[0],*right=&s[0]+p.size()-1;
    
    for(left;right<=&s[s.size()-1];left++,right++)//
    {      int k=left-&s[0];
         d.clear();
            char *m=left;
         for(m;m<=right;m++)//
            d.push_back(*m);
        for(int i=0;i<d.size();i++)
        {
        c[d[i]-'a']+=1;
         }
        for(char zifu:b)
       {
        c[zifu-'a']-=1;
       }
        for(int i=0;i<26;i++)
        {
            if(c[i]!=0)
            {   for(int i=0;i<26;i++)
                c[i]=0;
                break;
                
            }
            if(i==25)
            a.push_back((left-&s[0])/sizeof(char));
        }
    }
      if(a.empty())
        return vector<int>();
    return a;
    }
};
int main()
{
string s="";
string p="yqrbgjdwtcaxzsnifvhmou";
vector<int> a;
Solution *b=new Solution();
a=b->findAnagrams(s,p);
for(int i=0;i<a.size();i++)
cout<<a[i]<<endl;
}